var links = msg["payload"]
var convertdict = msg["payload2"]
var convertlink = []
for (const item of links) {
    var source = item[0]
    var source_phrase = source.split(":")
    var source_origin_node = source_phrase[0] 
    var source_origin_if = source_phrase[1] 

    for (const item2 of convertdict){
        if (item2.node.original == source_origin_node){
            var source_clab_node = item2.node.clab
            for (const interface of item2.iflist){
                if (interface.original.includes(source_origin_if)){
                    var source_clab_if = interface.clab
                }
            }
        }
    }


    var dest = item[1]
    var dest_phrase = dest.split(":")
    var dest_origin_node = dest_phrase[0]
    var dest_origin_if = dest_phrase[1] 

    for (const item2 of convertdict) {
        if (item2.node.original == dest_origin_node) {
            var dest_clab_node = item2.node.clab
            for (const interface of item2.iflist) {
                if (interface.original.includes(dest_origin_if)) {
                    var dest_clab_if = interface.clab
                }
            }
        }
    }
    var source_clab = source_clab_node.concat(':', source_clab_if)
    var dest_clab = dest_clab_node.concat(':', dest_clab_if)
    convertlink.push({"endpoints":[source_clab, dest_clab]})

}
msg.payload = convertlink
return msg;

